import React, { Component } from 'react';
import './Table.css';

class Table extends Component {

    constructor(props){

        super(props);
        this.getHeader = this.getHeader.bind(this);

    }

    colClickHandler = (key) => {

        this.props.data.rowData.sort((a, b) => {
            //const asc = this.state.directionSymbol[key] === 'asc';
           const asc= true;
           //console.log(a);debugger;
            if (a.data[3].id < a.data[3].id) {
                return asc ? -1 : 1;
            } else if (a[key] > b[key]) {
                return asc ? 1 : -1;
            } else {
                return 0;
            }
        })
    }

    getHeader = function(){
        return this.props.data.columnHeaders.map((colheaderData,ind) => {
            return (

                <th key={colheaderData.id} id={colheaderData.id} label={colheaderData.label}
                    data-sortable={colheaderData.sortable}
                    data-filterable={colheaderData.filterable} onClick={()=> { this.colClickHandler(colheaderData.id) }}> 
                    {colheaderData.label} 
                </th>
            )
        })


    }

    getRowsData = function(){
        return this.props.data.rowData.map(rowData => {
            return (
                <tr key={rowData.id} id={rowData.id}>
                    {rowData.data.map((rowDataConf) => {
                        return(
                            <td key={rowDataConf.id} label={rowDataConf.id}>
                                {rowDataConf.value}
                            </td>
                        )
                    })}
                </tr>
            )
        })


    }
    render() {
            return (
                <div>
                    <table>
                        <thead>
                            <tr>{this.getHeader()}</tr>
                        </thead>
                        <tbody>
                            {this.getRowsData()}
                        </tbody>
                    </table>
                </div>

            );
        }
    }

export default Table;
