import React, { Component } from 'react';
import './Table.css';

class Table extends Component {
    state = {
        tableData : null,
        columnData : null,
        rowData : null
    }

    constructor(props){

        super(props);
        this.setState({ 
            tableData : props.data,
            columnData : props.data.columnHeaders,
            rowData : props.data.rowData
         });
         debugger;
        this.getHeader = this.getHeader.bind(this);

    }

    colClickHandler = (key) => {
        this.setState({

            rowData : this.state.rowData.sort((a, b) => {
                //const asc = this.state.directionSymbol[key] === 'asc';
            const asc= false;
            //console.log(a);debugger;
                if (a.data[3].value < b.data[3].value) {
                    return asc ? -1 : 1;
                } else if (a.data[3].value > b.data[3].value) {
                    return asc ? 1 : -1;
                } else {
                    return 0;
                }
            })


        });
    }

    getHeader = function(){
        return this.state.columnData.map((colheaderData,ind) => {
            return (

                <th key={colheaderData.id} id={colheaderData.id} label={colheaderData.label}
                    data-sortable={colheaderData.sortable}
                    data-filterable={colheaderData.filterable} onClick={()=> { this.colClickHandler(colheaderData.id) }}> 
                    {colheaderData.label} 
                </th>
            )
        })


    }

    getRowsData = function(){
        return this.state.rowData.map(rowData => {
            return (
                <tr key={rowData.id} id={rowData.id}>
                    {rowData.data.map((rowDataConf) => {
                        return(
                            <td key={rowDataConf.id} label={rowDataConf.id}>
                                {rowDataConf.value}
                            </td>
                        )
                    })}
                </tr>
            )
        })


    }
    render() {
            return (
                <div>
                    <table>
                        <thead>
                            <tr>{this.getHeader()}</tr>
                        </thead>
                        <tbody>
                            {this.getRowsData()}
                        </tbody>
                    </table>
                </div>

            );
        }
    }

export default Table;
