import React, { Component } from 'react';
import './Table.css';

class Table extends Component {

    constructor(props){

        super(props);
        this.getHeader = this.getHeader.bind(this);
        this.getRowsData = this.getRowsData.bind(this);
    }



    getHeader = function(){
        //var keys = this.getKeys();
        return this.props.data.columnHeaders.map((colheaderData,ind) => {
            return (

                <th key={colheaderData.id} id={colheaderData.id} label={colheaderData.label}
                    data-sortable={colheaderData.sortable}
                    data-filterable={colheaderData.filterable} onClick={()=>{this.colClickHandler(colheaderData.id,this.tableJSON)}}> 
                    {colheaderData.label} 
                </th>
            )
        })


    }

    getRowsData = function(){
        var items = this.props.data;
        var keys = this.getKeys();
        return items.map((row, index)=>{
        return <tr key={index}><RenderRow key={index} data={row} keys={keys}/></tr>
        })

    }



    render() {
            return (
                <div>
                <table>
                <thead>
                <tr>{this.getHeader()}</tr>
                </thead>
                <tbody>
                {this.getRowsData()}
                </tbody>
                </table>
                </div>

            );
        }
    }
    const RenderRow = (props) =>{
        return props.keys.map((key, index)=>{
        return <td key={props.data[key]}>{props.data[key]}</td>
        })
     }
export default Table;
