import React, { Component } from 'react';
import './Table.css';

class Table extends Component {
    state = {
        tableData : this.props.data,
        columnData : this.props.data.columnHeaders,
        rowData : this.props.data.rowData,
        searchedTerm : ''
    }

    constructor(props){
        super(props);
        this.getHeader = this.getHeader.bind(this);
        this.handleSearch = this.handleSearch.bind(this);
    }

    handleSearch = (e) => {

        this.filterSearch(e.target.value);
    }

    filterSearch = (term) => {

        this.setState({
            SearchedTerm : term.trim().toLowerCase() 
        });
       if( term.length > 2){
            const filteredData = this.state.rowData.filter((row) => {
                var flag = false;
               data.forEach(function (element) {
                    if(element.value.toString().trim().toLowerCase().match(term)){
                        flag = true;
                    }
                  });
                  if(flag==true){
                    return true;
                  }  else{
                      return false;
                  }

                })
        }


    }
    colClickHandler = (key,index) => {
        this.setState({
            rowData : this.state.rowData.sort((a, b) => {
                const asc= true;
                if (a.data[index].value < b.data[index].value) {
                    return asc ? -1 : 1;
                } else if (a.data[index].value > b.data[index].value) {
                    return asc ? 1 : -1;
                } else {
                    return 0;
                }
            })  

        });
    }

    getHeader = function(){
        return this.state.columnData.map((colheaderData,index) => {
            return (
                <th key={colheaderData.id} id={colheaderData.id} label={colheaderData.label}
                    data-sortable={colheaderData.sortable}
                    data-filterable={colheaderData.filterable}> 
                    {   colheaderData.label }
                    {   colheaderData.sortable === "true" &&
                        <button onClick = { () => { this.colClickHandler(colheaderData.id,index); } }> click
                        </button> 
                    }
                </th>
            )
        })


    }

    getRowsData = function(){
        return this.state.rowData.map(rowData => {
            return (
                <tr key={rowData.id} id={rowData.id}>
                    {rowData.data.map((rowDataConf) => {
                        return(
                            <td key={rowDataConf.id} label={rowDataConf.id}>
                                {rowDataConf.value}
                            </td>
                        )
                    })}
                </tr>
            )
        })


    }
    render() {

            return (
                <div className="container">
                    <input type="text" onChange={this.handleSearch} placeholder="Type here" />
                    <table>
                        <thead>
                            <tr>{this.getHeader()}</tr>
                        </thead>
                        <tbody>
                            {this.getRowsData()}
                        </tbody>
                    </table>
                </div>

            );
        }
    }

export default Table;
