import React, { Component } from 'react';
import './Table.css';

class Table extends Component {
    state = {
        tableData : this.props.data,
        columnData : this.props.data.columnHeaders,
        rowData : this.props.data.rowData
    }

    constructor(props){
        super(props);
        this.getHeader = this.getHeader.bind(this);
        this.handleSearch = this.handleSearch.bind(this);
    }

    handleSearch = (e) => {

        this.filterSearch(e.target.value);
    }
    htmlDecode(input){
        console.log(input);
        var e = document.createElement('div');
        e.innerHTML = input;
        console.log(e.childNodes[0].nodeValue);
        return e.childNodes.length === 0 ? "" : e.childNodes[0].nodeValue;
    }

    filterSearch = (term) => {
        var tmp_term = term.toString();
        this.setState({
           rowData : this.state.tableData.rowData
        });
        term = term.toString().trim().toLowerCase();

       if( term.length > 2){
            var filteredData = this.state.rowData.filter((rows) => {
                let flag = false;
                rows.data.forEach(function (row) {
                    if(row.value.toString().trim().toLowerCase().match(term)){
                        flag = true;
                    }
                });
                if(flag===true){
                    return true;
                }  else{
                    return false;
                }
            })

            filteredData.map((rows,key1) => {
                rows.data.map( (row,key2) => {
                    filteredData[key1].data[key2].value = this.htmlDecode(row.value.toString().replace(tmp_term ,"<span class='highlight'>"+tmp_term+"</span>"))
                    return true
                })
                return true
            })
            this.setState({
                rowData : filteredData
            })
        }


    }

    getHeader = function(){
        return this.state.columnData.map((colheaderData,index) => {
            return (
                <th key={colheaderData.id} id={colheaderData.id} label={colheaderData.label}
                    data-sortable={colheaderData.sortable}
                    data-filterable={colheaderData.filterable}> 
                    {   colheaderData.label }
                    {   colheaderData.sortable === "true" &&
                        <button onClick = { () => { this.colClickHandler(colheaderData.id,index); } }> click
                        </button> 
                    }
                </th>
            )
        })


    }

    getRowsData = function(){
        return this.state.rowData.map(rowData => {
            return (
                <tr key={rowData.id} id={rowData.id}>
                    {rowData.data.map((rowDataConf) => {
                        return(
                            <td key={rowDataConf.id} label={rowDataConf.id}>
                                { rowDataConf.value }
                            </td>
                        )
                    })}
                </tr>
            )
        })


    }
    render() {

            return (
                <div className="container">
                    <input type="text" onChange={this.handleSearch} placeholder= "Search Here" />
                    <table>
                        <thead>
                            <tr>{this.getHeader()}</tr>
                        </thead>
                        <tbody>
                            {this.getRowsData()}
                        </tbody>
                    </table>
                </div>

            );
        }
    }

export default Table;
