import React, { Component } from 'react';
import './Table.css';
//import tableJSON from "../Assets/"
class Table extends Component {
    state = {}

    componentDidMount () {
        fetch('/Assets/table-json.json')
          .then(res => res.json())
          .then(this.onLoad);
    }
    onLoad = (response) => {
        this.setState({
            tableJSON: response.data
        });
    }
    colClickHandler = (filterable,sortable,id,indexIdentifier) => {
        /*if(sortable){
            let upDatedRowSON;
            upDatedRowSON = tableJSON.rowData.sort((a,b)=>{
                return (a.data[indexIdentifier].value)-b.data[indexIdentifier].value
            })
            console.log(upDatedtableJSON);
            var tableJSON={...tableJSON,}
            this.setState({
                tableJSON : upDatedtableJSON
            })
        }*/
    }
    render() {
        let tableheader = null, tablerows = null;
        tableheader = this.state.tableJSON.columnHeaders.map((colheaderData,ind) => {
            return (
                <th key={colheaderData.id} id={colheaderData.id} label={colheaderData.label}
                    data-sortable={colheaderData.sortable}
                    data-filterable={colheaderData.filterable} onClick={()=>{this.colClickHandler(colheaderData.filterable,colheaderData.sortable,colheaderData.id,ind)}}> 
                    {colheaderData.label} 
                </th>
            )
        })
        tablerows = this.state.tableJSON.rowData.map(rowData => {
            return (
                <tr key={rowData.id} id={rowData.id}>
                    {rowData.data.map((rowDataConf) => {
                        return(
                            <td label={rowDataConf.id}>
                                {rowDataConf.value}
                            </td>
                        )
                    })}
                </tr>
            )
        })

        return (
            <div className="Table-wraper">
                <table>
                    <tr>
                        {tableheader}
                    </tr>
                    <tbody>
                        {tablerows}
                    </tbody>
                </table>
            </div>
        );
    }
}
export default Table;
