import React, { Component } from 'react';
import './Table.css';
import * as data from "../Assets/table-json.json";
class Table extends Component {
    state = {
        tableJSON : data.default
    }
    //colClickHandler = (filterable,sortable,id,indexIdentifier) => {
        colClickHandler = (key,data) => {
            this.setState({
                tableJSON : this.state.tableJSON.rowData.sort((a, b) => {
                    //const asc = this.state.directionSymbol[key] === 'asc';
                    const asc= true;
                    if (a[key] < b[key]) {
                        return asc ? -1 : 1;
                    } else if (a[key] > b[key]) {
                        return asc ? 1 : -1;
                    } else {
                        return 0;
                    }
                }),

                direction: {
                    [key]: this.state.direction[key] === 'asc'
                    ? 'desc'
                    : 'asc'
                }
            })
        }

        /*if(sortable){
            let upDatedtableJSON;
            upDatedRowSON = tableJSON.rowData.sort((a,b)=>{
                return (a.data[indexIdentifier].value)-b.data[indexIdentifier].value
            })
            console.log(upDatedtableJSON);

            this.setState({
                tableJSON : upDatedtableJSON
            })
        }*/
    //}
    render() {
        let tableheader = null, tablerows = null;

        tableheader = this.state.tableJSON.columnHeaders.map((colheaderData,ind) => {
            return (

                <th key={colheaderData.id} id={colheaderData.id} label={colheaderData.label}
                    data-sortable={colheaderData.sortable}
                    data-filterable={colheaderData.filterable} onClick={()=>{this.colClickHandler(colheaderData.id,this.tableJSON)}}> 
                    {colheaderData.label} 
                </th>
            )
        })
        tablerows = this.state.tableJSON.rowData.map(rowData => {
            return (
                <tr key={rowData.id} id={rowData.id}>
                    {rowData.data.map((rowDataConf) => {
                        return(
                            <td key={rowDataConf.id} label={rowDataConf.id}>
                                {rowDataConf.value}
                            </td>
                        )
                    })}
                </tr>
            )
        })

        return (
            <div className="Table-wraper">
                <table>
                    <thead>
                        <tr>
                            {tableheader}
                        </tr>
                    </thead>
                    <tbody>
                        {tablerows}
                    </tbody>
                </table>
            </div>
        );
    }
}
export default Table;
